import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import * as serviceWorker from './serviceWorker';
//redux
import {createStore} from 'redux';
import {Provider} from 'react-redux';
import todos from './Reducers/todos';
import {normalizeTodos} from './Schemas/index';

const API = {
  types: [
    { name: 'started', 
      id: 0,
      todos: [
        {text: 'started' , done: false, id: 0},
        {text: 'started' , done: false, id: 12},
        {text: 'started' , done: false, id: 34},
      ]
    },
    { name: 'progress', 
      id: 5,
      todos: [
        {text: 'progress' , done: false, id: 1},
        {text: 'started' , done: false, id: 22},
      ]
    },
    { name: 'finished', 
      id: 2,
      todos: [
        {text: 'fished' , done: false, id: 2},
        {text: 'started' , done: false, id: 90},
      ]
    },
  ]
}
const normalizedData = normalizeTodos(API);

const initialState = {
  data: {
    entities: normalizedData.entities,
    todoTypes: normalizedData.result,
  }
}

const store = createStore(todos, initialState, window.__REDUX_DEVTOOLS_EXTENSION__ && window.__REDUX_DEVTOOLS_EXTENSION__()); 
export default store;

ReactDOM.render(
  <Provider store={store}>
    <App/>
  </Provider>,
   document.getElementById('root'));

// If you want your app to work offline and load faster, you can change
// unregister() to register() below. Note this comes with some pitfalls.
// Learn more about service workers: http://bit.ly/CRA-PWA
serviceWorker.unregister();
