import {normalize, schema} from 'normalizr';

const API = {
  id: 1,
  title: "Blog Title",
  content: "Some really short blog content.  Actually the least interesting post ever",
  created_at: "2016-01-10T23:07:43.248Z",
  updated_at: '2016-01-10T23:07:43.248Z',
  author: {
      id: 81,
      name: "Mr Shelby"
  },
  comments: [
      {
          id: 352,
          content: "First!",
          author: {
              id: 41,
              name: "Foo Bar"
          }
      }
  ]
}

export const normalizeTodos = () => {

  const postSchema = new schema.Entity('posts', { idAttribute: 'id' });
  const postAuthorSchema = new schema.Entity('postAuthors', { idAttribute: 'id' });
  const commentSchema = new schema.Entity('comments', { idAttribute: 'id' });
  const CommentAuthorSchema = new schema.Entity('commentAuthors', { idAttribute: 'id' });

  // Here we define relationships between each resource/schema/entity
  // (or whatever you feel like calling them these days)

  // The post resource in our API response has an author and comments as children
  postSchema.define({
      author: postAuthorSchema,
      comments: [ commentSchema ]
  });

  // Each comment has an author
  commentSchema.define({
      author: CommentAuthorSchema
  });

  const result = normalize(API, postSchema);
  console.log(result);
}
