import {normalize, schema} from 'normalizr';

const API = {
  id: 1,
  title: "Blog Title",
  content: "Some really short blog content.  Actually the least interesting post ever",
  created_at: "2016-01-10T23:07:43.248Z",
  updated_at: '2016-01-10T23:07:43.248Z',
  author: {
      id: 81,
      name: "Mr Shelby"
  },
  comments: [
      {
          id: 352,
          content: "First!",
          author: {
              id: 41,
              name: "Foo Bar"
          }
      }
  ]
}

export const normalizeTodos = (data) => {

  const postSchema = new schema.Entity('posts');
  const postAuthorSchema = new schema.Entity('postAuthors');
  const commentSchema = new schema.Entity('comments');
  const CommentAuthorSchema = new schema.Entity('commentAuthors');

  const todo = new schema.Entity('todos');
  const type = new schema.Entity('todoType', {
    todos: [ todo ]
  });

  //const typeList = {types: [ type ]}

  const result = normalize(data, type);
  console.log(result);
}
