import {normalize, schema} from 'normalizr';

export const normalizeTodos = (store) => {

  const todo = new schema.Entity('todos');

  const todoList = new schema.Array('todoList', {
    todos: [ todo ]
  })
  
  //const flattened = { stages: [ stage ]};
  
  const normalizedData = normalize(store, [ todoList ] );

  return normalizedData;
}
