function todos(state, action){
  switch(action.type){
    case 'ADD_TODO':{
      return [
         ...state,
         {
          text: "hola",
          done: false,
         }
        ]
    }
    case 'REMOVE_TODO':
      return
    default:
      return state 
  }
}

export default todos;
