function todos(state, action){
  switch(action.type){
    case 'ADD_TODO':
    const todosLength = Object.getOwnPropertyNames(state.entities.todos).length;
      return {
        ...state, 
        entities: {...state.entities, 
                  todos: {...state.entities.todos, [todosLength]: {text: action.payload.todoText, done: false, id: todosLength}},
                  todoType: {...state.entities.todoType, 0: { ...state.entities.todoType[0], todos: [...state.entities.todoType[0].todos, todosLength]}}
        }
      }
    case 'REMOVE_TODO':
      return {
        ...state,
        todos: state.todos.filter(todo => todo.id !== action.payload.id)
      }
    case 'CHECK_TODO':
    return {
      ...state, 
      entities: {...state.entities, 
                todos: {...state.entities.todos, [action.payload.id]: {...state.entities.todos[action.payload.id], done: !done }}
      }
    }
      return {
        ...state,
        todos: state.todos.map(todo => todo.id === action.payload.id ? {...todo, done: !todo.done }: {...todo})
      } 
    default:
      return state 
  }
}

export default todos;
