import React, { Component } from 'react';
import './style.css';
//redux
import { connect } from 'react-redux';

class Todo extends Component {
  
  componentDidMount (){
    console.log(this.props)
  }

  done = () => {
    this.props.dispatch({
      type: 'CHECK_TODO',
      payload: {
        id: this.props.data.id
      }
    })
  }

  remove = () => {
    this.props.dispatch({
      type: 'REMOVE_TODO',
      payload: {
        id: this.props.data.id
      }
    })
  }
  
  render() {
    return (
      {/*}  <div className="todoContainer" onClick={this.done}>
        <li className={this.props.data.done ? 'done' : null }>{this.props.data.text}</li>
        <img src="https://cdn2.iconfinder.com/data/icons/round-interface-1/217/50-512.png" width="30px" onClick={this.remove} alt="asd"/>
    </div> */}
    );
  }
}

export default connect()(Todo);
