function todos(state, action){
  switch(action.type){
    case 'ADD_TODO':
      console.log(action.payload)
      return {
        ...state, 
        entities: {...state.entities, 
                  todos: {...state.entities.todos, 900: {text: action.payload.txt, done: false, id: 900}}
        }
        /* ...state,
         entities: {...entities, 
            todos: [...state.todos, {
              text: action.payload.todoText,
              done: false,
              id: state.entities.todos.length
            }],
            todoType: {...todoType, 0: {...0, todos: todos.push(state.entities.todos.length)} }
          },*/
      }
    case 'REMOVE_TODO':
      return {
        ...state,
        todos: state.todos.filter(todo => todo.id !== action.payload.id)
      }
    case 'CHECK_TODO':
      return {
        ...state,
        todos: state.todos.map(todo => todo.id === action.payload.id ? {...todo, done: !todo.done }: {...todo})
      } 
    default:
      return state 
  }
}

export default todos;
