import {normalize, schema} from 'normalizr';

export const normalizeTodos = (store) => {

  const todo = new schema.Entity('todos', {}, {
    idAttribute: 'id',
    processStrategy: (value, parent, key) => ({...value, id: parent.id })
  });
  
  const ids = new schema.Entity('ids', {
    todos: new schema.Array(todo)
  });
  
  const flattened = { ids: new schema.Array(ids)};
  
  const normalizedData = normalize(store, flattened);

  return normalizedData;
}
