import React, { Component } from 'react';
import '../styles.css';
import Button from '../../../Components/Button/index';
import TextInput from '../../../Components/TextInput/index';
import TodoList from '../Components/TodoList/index';

class Search extends Component {
  constructor(props){
    super(props);
    this.state = {
      todos: [{text: 'create todos', done: false},{text: 'get rich', done: true}],
      filteredTodos: [],
      text: '',
    }
  }

  textChange = text => {
    this.setState({
      text,
      filteredTodos: this.state.todos.filter( todo => todo.text.contains(this.state.text))
    })
  }

  search = () => {
    console.log("hey")
  }

  render() {
    return (
      <div className="container">
        <section className="panel">
          <section className="input">
              <TextInput textChange={this.textChange.bind(this)} value={this.state.text}/>
              <Button search={this.search.bind(this)} text="Search"/>
            </section>
          <section className="todoList">
            <TodoList todos={this.state.filteredTodos}/>
          </section>
        </section>
      </div>
    );
  }
}

export default Search;
