import React, { Component } from 'react';
import './style.css';
//redux
import { connect } from 'react-redux';

class Todo extends Component {
  
  done = () => {
    this.props.dispatch({
      type: 'CHECK_TODO',
      payload: {
        id: this.props.data.id
      }
    })
  }
  
  render() {
    return (
        <div className="todoContainer" onClick={this.done}>
        <li className={this.props.data.done ? 'done' : null }>{this.props.data.text}</li>
        <p>hola</p>
        </div>
    );
  }
}

export default connect()(Todo);
