import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import * as serviceWorker from './serviceWorker';
//redux
import {createStore} from 'redux';
import {Provider} from 'react-redux';
import todos from './Reducers/todos';
import {normalizeTodos} from './Schemas/index';

const initialState = {
    started: [
      {text: 'started' , done: false, id: 0}
    ],
    progress: [
      {text: 'progress' , done: false, id: 1}
    ],
    finished: [
      {text: 'finished' , done: false, id: 2}
    ]
}
normalizeTodos(initialState);

const store = createStore(todos, initialState, window.__REDUX_DEVTOOLS_EXTENSION__ && window.__REDUX_DEVTOOLS_EXTENSION__()); 
export default store;

ReactDOM.render(
  <Provider store={store}>
    <App/>
  </Provider>,
   document.getElementById('root'));

// If you want your app to work offline and load faster, you can change
// unregister() to register() below. Note this comes with some pitfalls.
// Learn more about service workers: http://bit.ly/CRA-PWA
serviceWorker.unregister();
