import React, { Component } from 'react';
import InputContainer from './Components/InputContainer';
import TodoList from '../Components/TodoList/index';

import '../styles.css';

class Home extends Component {

  constructor(props){
    super(props)
    this.state = {
      todoTypes: []
    }
  }

  componentWillMount(){
    const todoTypes = this.props.data.todoTypes.map((todoTypeId) => this.props.data.entities.todoType[todoTypeId])
    this.setState({
      todoTypes
    })
  }

  render() {
    return (
      <div className="container">
        <section className="panel">
          <section className="input">
            <InputContainer/>
          </section>
          <section className="todoList">
            <TodoList TodoList={this.state.todoTypes[0]}/> 
          </section>
        </section>
      </div>
    );
  }
}

export default Home;
