function todos(state, action){
  switch(action.type){
    case 'ADD_TODO':
      return {
         ...state,
         todos: [...state.todos, {
          text: action.payload.todoText,
          done: false,
          id: state.todos.length
         }]
      }
    case 'REMOVE_TODO':
      return
    case 'CHECK_TODO':
      return {
        ...state,
        todos: "asd"
      } 
    default:
      return state 
  }
}

export default todos;
