import {normalize, schema} from 'normalizr';

export const normalizeTodos = (data) => {

 /* const todo = new schema.Entity('todos', {idAttribute: 'id'});
  const type = new schema.Entity('todoType', {idAttribute: 'id'}, {
    todos: [ todo ]
  }); 

  const typeList = {types: [ type ]};

  const result = normalize(data, typeList); */

  const API = {
    todos: [
      {
        text: "hola",
        id: 1,
      },
      {
        text: "asd",
        id: 4,
      },
      {
        text: "asffff",
        id: 23,
      },
    ]
  }

  const todo = new schema.Entity('todo');
  const todoList = {todoList: [ todo ]}

  const result = normalize(API, todoList)


  return result;
}
