import React from 'react';
import './style.css';

function Modal(props){
  return(
    <div className="modal">
      <p>estyo es un modal</p>
      {props.children}
      <button onClick={props.handleCancelClick}>Cancel</button>
    </div>
  )
}

export default Modal;
