import {normalize, schema} from 'normalizr';

export const normalizeTodos = ({todos}) => {

  const todo = new schema.Entity('todos');

  const todoList = new schema.Entity('todoList', {
    todos: [ todo ]
  })
  
  //const flattened = { stages: [ stage ]};
  
  const normalizedData = normalize(todos, [ todoList ] );

  return normalizedData;
}
